function changeYRange (d, base) {
	var ratio = 1.0;
	if (d > 0) {
		ratio = Math.pow(base, d);
	} else if (d < 0) {
		ratio = Math.pow(base, d);
	}
	var tmp = m_ag.orthoYRange * ratio;
	if (tmp < m_ag.Camera_YRange_Min) {
		tmp = m_ag.Camera_YRange_Min;
	}
	m_ag.orthoYRange = tmp;
}

