function calcRotateAxisDeg (upVec) {

	var V = [upVec.x,upVec.y,upVec.z];

	var X = [1.0,0.0,0.0];// 
	var Y = [0.0,1.0,0.0];// 
	var Z = [0.0,0.0,1.0];// 
	var XV,YV,ZV,VV; //_
	var VL;          //_|V|
	var k;           //_x,y,z
	var pi;          //_
	var rx,ry,rz;    //_
	var dx,dy,dz;    //_

	//_
	XV = YV = ZV = VV = 0;
	for(k=0;k<3;k++){
		XV += X[k]*V[k];
		YV += Y[k]*V[k];
		ZV += Z[k]*V[k];
		VV += V[k]*V[k];
	}
	//_
	VL = Math.sqrt(VV);
	rx = Math.acos(XV/VL);
	ry = Math.acos(YV/VL);
	rz = Math.acos(ZV/VL);
	//_
	pi = Math.PI;
	dx = 180*rx/pi;
	dy = 180*ry/pi;
	dz = 180*rz/pi;
	//_
//	_dump("X=["+rx+"]["+dx+"]");
//	_dump("Y=["+ry+"]["+dy+"]");
//	_dump("Z=["+rz+"]["+dz+"]");


	var degH = 0;
	var degV = 0;

	var CTx = upVec.x;
	var CTy = upVec.y;
	var CTz = upVec.z;

//	_dump("CTx=["+CTx+"]");
//	_dump("CTy=["+CTy+"]");
//	_dump("CTz=["+CTz+"]");

	// Calculate Rotate H
	var radH = Math.acos(CTy / Math.sqrt(CTx*CTx + CTy * CTy));
//	_dump("radH=["+radH+"]");
	if(isNaN(radH)) radH = 0;
	var degH = radH / Math.PI * 180;

	// Calculate Rotate V
	var UnitX = -1 * Math.sin(degH / 180 * Math.PI);
	var UnitY = Math.cos(degH / 180 * Math.PI);
	var UnitZ = 0;
	var radV = Math.acos((CTx * UnitX + CTy * UnitY + CTz * UnitZ) / Math.sqrt((CTx * CTx + CTy * CTy + CTz * CTz) * (UnitX * UnitX + UnitY * UnitY + UnitZ * UnitZ)));
	if(isNaN(radV)) radV = 0;
	var degV = radV / Math.PI * 180;

//	_dump("deg=["+degH+"]["+degV+"]");


	return {H:degH,V:degV};
}

