//_dump("selectPathCB():gParams.fmaid=["+(!Ext.isEmpty(gParams.fmaid))+"]["+gParams.fmaid+"]");
//_dump("selectPathCB():gParams.txpath=["+(!Ext.isEmpty(gParams.txpath))+"]["+gParams.txpath+"]");
