						records[i].beginEdit();
						records[i].set('partslist',partslist);
						records[i].set('zoom',zoom);
						records[i].set('exclude',exclude);
						records[i].set('color',color?color:'#'+ (elem_type=='bp3d_point'? prm_record.data.point_color_rgb:prm_record.data.color_rgb));
